  function adjust_pictures_size(image) {
    var isProportional = (image.dataset.proportional === 'true');

    if (isProportional) {
      image.style.height = 'auto';
      return this;
    }

    if (image.dataset.ratio === 'true') {
      image.style.height = ($(image).width() * image.dataset.ratio) + 'px'
      return this;
    }
  };
